interface Item {
    id: number;
    name: string;
    price: number;
    quantity: number;
    image?:string;
  }
  const Store: Item[] = [
    { id: 11111, name: "xbox", price: 2500, quantity: 4, image:"data:image/jpeg;base64,"},
    { id: 22222, name: "ps", price: 3500, quantity: 3,image:"data:image/jpeg;base64," },
    { id: 33333, name: "cat", price: 1000, quantity: 2 ,image:'data:image/jpeg;base64,'},
    { id: 44444, name: "ipad", price: 2950, quantity: 1,image:'data:image/jpeg;base64,' },
  ];
  function createItem(name: string,price:number,quantity:number, image?: string): Item {
    return {id:(Math.random()*1000),name,price,quantity,image };
}
Store.push(createItem("bottle",10,5,'data:image/jpeg;base64,'))

function renderStore(){
    try{
    const store = document.querySelector('#items') as HTMLElement;
    if (!store) throw new Error('Could not find an element with the id "store"');
    Store.forEach(item => {   
        //create an article element
        const oneItem = document.createElement('article');
        
        //set the innerHTML of the article element to the pet's name and image
        oneItem.innerHTML = `
        <img src="${item.image}" />
        <h1>${item.name}: ${item.id}</h1>
        <h2>price: ${item.price} $<h2>
        <h3>left in store: ${item.quantity}<h3>

    `;
        //add the class pet to the article element
        oneItem.classList.add('continer');
        //append the article element to the pets element
        store.appendChild(oneItem);
    });

    }catch(e){
        console.log(e)
    }

}
renderStore();